        baselines: baselines.map(baseline => ({
          area: baseline.area,
          eventType: baseline.eventType,
          timeWindow: baseline.timeWindow,
          meanValue: baseline.meanValue,
          standardDeviation: baseline.standardDeviation,
          sampleCount: baseline.sampleCount
        }))
