import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Shield, ShieldCheck, DollarSign, TrendingUp, Settings, Users, ArrowRight, AlertCircle, LogIn } from "lucide-react";
import { Link } from "wouter";

type Agent = {
  id: string;
  name: string;
  isActive: boolean;
  status: string; // active, coming_soon, maintenance
  sector: string;
  description: string;
  baseRoute: string;
  icon: string;
  minimumRole: string | null;
};

type UserAgentAccess = {
  id: string;
  userId: string;
  agentId: string;
  role: string;
  isActive: boolean;
  agent: Agent;
  grantedAt: string;
};

// Agent icons mapping by icon name
const iconMapping = {
  shield: Shield,
  'shield-check': ShieldCheck,
  'dollar-sign': DollarSign,
  'trending-up': TrendingUp,
  settings: Settings,
  users: Users,
};

export default function PlatformDashboard() {
  const { data: agents = [], isLoading: agentsLoading, error: agentsError } = useQuery<Agent[]>({
    queryKey: ["/api/agents"],
    queryFn: async () => {
      const response = await fetch("/api/agents", { credentials: "include" });
      if (!response.ok) {
        if (response.status === 401) {
          throw new Error("UNAUTHORIZED");
        }
        throw new Error(`Failed to fetch agents: ${response.status}`);
      }
      return response.json();
    }
  });

  const { data: userAgents = [], isLoading: userAgentsLoading, error: userAgentsError } = useQuery<UserAgentAccess[]>({
    queryKey: ["/api/user/agents"],
    queryFn: async () => {
      const response = await fetch("/api/user/agents", { credentials: "include" });
      if (!response.ok) {
        if (response.status === 401) {
          throw new Error("UNAUTHORIZED");
        }
        throw new Error(`Failed to fetch user agents: ${response.status}`);
      }
      return response.json();
    }
  });

  // Handle authentication errors
  const isUnauthorized = agentsError?.message === "UNAUTHORIZED" || userAgentsError?.message === "UNAUTHORIZED";

  if (isUnauthorized) {
    return (
      <div className="container mx-auto p-6" data-testid="platform-dashboard-unauthorized">
        <div className="flex flex-col items-center justify-center min-h-[400px] space-y-6">
          <Alert className="max-w-md border-red-200 dark:border-red-800">
            <AlertCircle className="h-4 w-4 text-red-600 dark:text-red-400" />
            <AlertDescription className="text-red-700 dark:text-red-300">
              Authentication required. Please log in to access the platform.
            </AlertDescription>
          </Alert>
          <Link href="/login">
            <Button className="flex items-center space-x-2" data-testid="button-login">
              <LogIn className="w-4 h-4" />
              <span>Go to Login</span>
            </Button>
          </Link>
        </div>
      </div>
    );
  }

  // Handle general errors
  if (agentsError || userAgentsError) {
    return (
      <div className="container mx-auto p-6" data-testid="platform-dashboard-error">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
            Penny Multi-Agent Platform
          </h1>
          <p className="text-gray-600 dark:text-gray-300">
            Access and manage your business intelligence agents across different sectors
          </p>
        </div>

        <div className="space-y-4">
          {agentsError && (
            <Alert className="border-red-200 dark:border-red-800" data-testid="agents-error">
              <AlertCircle className="h-4 w-4 text-red-600 dark:text-red-400" />
              <AlertDescription className="text-red-700 dark:text-red-300">
                Failed to load available agents. {agentsError.message}
              </AlertDescription>
            </Alert>
          )}

          {userAgentsError && (
            <Alert className="border-red-200 dark:border-red-800" data-testid="user-agents-error">
              <AlertCircle className="h-4 w-4 text-red-600 dark:text-red-400" />
              <AlertDescription className="text-red-700 dark:text-red-300">
                Failed to load your agent access permissions. {userAgentsError.message}
              </AlertDescription>
            </Alert>
          )}

          <Button 
            onClick={() => window.location.reload()} 
            className="mt-4"
            data-testid="button-retry"
          >
            Retry Loading
          </Button>
        </div>
      </div>
    );
  }

  if (agentsLoading || userAgentsLoading) {
    return (
      <div className="container mx-auto p-6" data-testid="platform-dashboard-loading">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[...Array(6)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardHeader>
                <div className="w-8 h-8 bg-gray-300 dark:bg-gray-600 rounded mb-2"></div>
                <div className="w-32 h-6 bg-gray-300 dark:bg-gray-600 rounded"></div>
                <div className="w-48 h-4 bg-gray-300 dark:bg-gray-600 rounded"></div>
              </CardHeader>
              <CardContent>
                <div className="w-24 h-8 bg-gray-300 dark:bg-gray-600 rounded"></div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  // Create map of user's agent access for quick lookup
  const userAgentMap = new Map(userAgents.map(ua => [ua.agentId, ua]));

  const getAgentIcon = (iconName: string) => {
    const IconComponent = iconMapping[iconName as keyof typeof iconMapping] || Settings;
    return IconComponent;
  };

  return (
    <div className="container mx-auto p-6" data-testid="platform-dashboard">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2" data-testid="dashboard-title">
          Penny Multi-Agent Platform
        </h1>
        <p className="text-gray-600 dark:text-gray-300" data-testid="dashboard-description">
          Access and manage your business intelligence agents across different sectors
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {agents.map((agent) => {
          const userAccess = userAgentMap.get(agent.id);
          const hasAccess = userAccess && userAccess.isActive;
          const isComingSoon = agent.status === 'coming_soon';
          const isFullyAccessible = agent.status === 'active' && hasAccess;
          const IconComponent = getAgentIcon(agent.icon);

          return (
            <Card 
              key={agent.id} 
              className={`transition-all duration-200 hover:shadow-lg ${
                isFullyAccessible 
                  ? 'border-green-200 dark:border-green-800' 
                  : isComingSoon 
                  ? 'border-amber-200 dark:border-amber-800' 
                  : 'border-gray-200 dark:border-gray-700'
              }`}
              data-testid={`agent-card-${agent.id}`}
            >
              <CardHeader>
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className={`p-2 rounded-lg ${
                      isFullyAccessible 
                        ? 'bg-green-100 dark:bg-green-900 text-green-600 dark:text-green-400' 
                        : isComingSoon 
                        ? 'bg-amber-100 dark:bg-amber-900 text-amber-600 dark:text-amber-400'
                        : 'bg-gray-100 dark:bg-gray-800 text-gray-600 dark:text-gray-400'
                    }`}>
                      <IconComponent className="w-6 h-6" />
                    </div>
                    <div>
                      <CardTitle className="text-lg" data-testid={`agent-name-${agent.id}`}>
                        {agent.name}
                      </CardTitle>
                      <Badge 
                        variant={isComingSoon ? "secondary" : agent.isActive ? "default" : "secondary"}
                        className={`text-xs ${
                          isComingSoon ? 'bg-amber-100 text-amber-800 dark:bg-amber-900 dark:text-amber-200' : ''
                        }`}
                        data-testid={`agent-status-${agent.id}`}
                      >
                        {isComingSoon ? "Coming Soon" : agent.isActive ? "Active" : "Inactive"}
                      </Badge>
                    </div>
                  </div>
                  <div className="flex flex-col items-end">
                    {hasAccess && (
                      <Badge variant="outline" className="text-xs mb-1" data-testid={`user-role-${agent.id}`}>
                        {userAccess.role}
                      </Badge>
                    )}
                    <Badge 
                      variant={hasAccess ? "default" : "secondary"}
                      className="text-xs"
                      data-testid={`access-status-${agent.id}`}
                    >
                      {hasAccess ? "Access Granted" : "No Access"}
                    </Badge>
                  </div>
                </div>
                <CardDescription data-testid={`agent-description-${agent.id}`}>
                  {agent.description}
                </CardDescription>
              </CardHeader>
              <CardContent>
                {isComingSoon ? (
                  <Button 
                    variant="outline" 
                    className="w-full bg-amber-50 border-amber-200 text-amber-700 dark:bg-amber-950 dark:border-amber-800 dark:text-amber-300" 
                    disabled
                    data-testid={`button-coming-soon-${agent.id}`}
                  >
                    Coming Soon
                  </Button>
                ) : hasAccess ? (
                  <Link href={agent.baseRoute}>
                    <Button className="w-full" data-testid={`button-access-${agent.id}`}>
                      Access {agent.name}
                      <ArrowRight className="w-4 h-4 ml-2" />
                    </Button>
                  </Link>
                ) : (
                  <Button 
                    variant="outline" 
                    className="w-full" 
                    disabled
                    data-testid={`button-request-${agent.id}`}
                  >
                    Request Access
                  </Button>
                )}
              </CardContent>
            </Card>
          );
        })}
      </div>

      {agents.length === 0 && (
        <div className="text-center py-12" data-testid="no-agents-message">
          <p className="text-gray-500 dark:text-gray-400">No agents available at this time.</p>
        </div>
      )}
    </div>
  );
}

client/src/pages/operations-dashboard.ts.content:
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Settings, Package, Clock, CheckCircle, AlertCircle, Activity } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";
import { useQuery } from "@tanstack/react-query";

// TypeScript interfaces for Operations API response
interface ActiveProcess {
  id: string;
  name: string;
  status: string;
  progress: number;
  eta: string;
}

interface SystemMetric {
  name: string;
  status: string;
  efficiency: number;
}

interface RecentAlert {
  id: string;
  message: string;
  severity: string;
  time: string;
}

interface InfrastructureStatus {
  totalComponents: number;
  operational: number;
  maintenance: number;
  offline: number;
}

interface OperationsResponse {
  // Core operations metrics from database
  activeProcesses: number;
  completedTasks: number;
  efficiencyRate: number;
  systemUptime: number;
  avgResponseTime: number;
  infrastructureHealth: number;
  recentIncidents: number;
  totalProcesses: number;
  failedTasks: number;

  // Dashboard display data
  activeProcessesList: ActiveProcess[];
  systemMetrics: SystemMetric[];
  recentAlerts: RecentAlert[];
  infrastructureStatus: InfrastructureStatus;
  resourceUtilization: number;
  pendingApprovals: number;
}

export default function OperationsDashboard() {
  const { user } = useAuth();

  // Fetch operations data from backend API with proper typing
  const { data: operationsData, isLoading, error, refetch } = useQuery<OperationsResponse>({
    queryKey: ['/api/operations'],
    enabled: !!user
  });

  const operationsStats = operationsData || {} as OperationsResponse;
  const activeProcesses = operationsData?.activeProcessesList || [];
  const systemMetrics = operationsData?.systemMetrics || [];
  const recentAlerts = operationsData?.recentAlerts || [];

  const getStatusColor = (status: string) => {
    switch (status) {
      case "operational": return "text-green-600";
      case "maintenance": return "text-yellow-600";
      case "running": return "text-blue-600";
      case "pending": return "text-orange-600";
      case "completed": return "text-green-600";
      default: return "text-gray-600";
    }
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case "success": return "text-green-600 bg-green-50 border-green-200";
      case "warning": return "text-orange-600 bg-orange-50 border-orange-200";
      case "info": return "text-blue-600 bg-blue-50 border-blue-200";
      case "error": return "text-red-600 bg-red-50 border-red-200";
      default: return "text-gray-600 bg-gray-50 border-gray-200";
    }
  };

  if (isLoading) {
    return (
      <div className="p-6 space-y-6">
        <div className="text-center" data-testid="loading-state">
          <p>Loading operations data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6 space-y-6">
        <div className="text-center text-red-600" data-testid="error-state">
          <p>Error loading operations data. Please try again.</p>
          <Button onClick={() => refetch()} className="mt-2" data-testid="button-retry">
            Retry
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-purple-800 dark:text-purple-400" data-testid="operations-dashboard-title">
            Operations Dashboard
          </h1>
          <p className="text-muted-foreground">Monitor and optimize business operations in real-time</p>
          {user && <p className="text-sm text-muted-foreground">Operations Manager: {user.username}</p>}
        </div>
        <div className="flex items-center gap-2">
          <Badge variant="outline" className="text-green-600">
            <Activity className="w-4 h-4 mr-1" />
            {operationsStats.efficiencyRate || 0}% Efficiency
          </Badge>
          <Button onClick={() => refetch()} variant="outline" size="sm" data-testid="button-refresh">
            Refresh
          </Button>
        </div>
      </div>

      {/* Operations Overview Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card className="border-purple-200">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Active Processes</CardTitle>
            <Settings className="h-4 w-4 text-purple-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-purple-700" data-testid="active-processes">
              {operationsStats.activeProcesses || 0}
            </div>
            <p className="text-xs text-muted-foreground">{operationsStats.completedTasks || 0} completed total</p>
          </CardContent>
        </Card>

        <Card className="border-green-200">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">System Uptime</CardTitle>
            <CheckCircle className="h-4 w-4 text-green-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-green-700" data-testid="system-uptime">
              {operationsStats.systemUptime || 0}%
            </div>
            <p className="text-xs text-muted-foreground">Last 30 days</p>
          </CardContent>
        </Card>

        <Card className="border-blue-200">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Completed Tasks</CardTitle>
            <Package className="h-4 w-4 text-blue-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-blue-700" data-testid="completed-tasks">
              {operationsStats.completedTasks || 0}
            </div>
            <p className="text-xs text-muted-foreground">This week</p>
          </CardContent>
        </Card>

        <Card className="border-orange-200">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Pending Approvals</CardTitle>
            <Clock className="h-4 w-4 text-orange-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-orange-700" data-testid="pending-approvals">
              {operationsStats.pendingApprovals || 0}
            </div>
            <p className="text-xs text-muted-foreground">Require attention</p>
          </CardContent>
        </Card>
      </div>

      {/* Resource Utilization */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Activity className="w-5 h-5 mr-2 text-purple-600" />
            Resource Utilization
          </CardTitle>
          <CardDescription>Current system and workforce capacity usage</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-2">
            <div className="flex justify-between text-sm">
              <span>Capacity Used</span>
              <span>{operationsStats.resourceUtilization || 0}%</span>
            </div>
            <Progress value={operationsStats.resourceUtilization || 0} className="h-3" />
            <p className="text-xs text-muted-foreground">
              Optimal range: 80-90% utilization
            </p>
          </div>
        </CardContent>
      </Card>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Active Processes */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <Settings className="w-5 h-5 mr-2 text-blue-600" />
              Active Processes
            </CardTitle>
            <CardDescription>Currently running operational workflows</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {activeProcesses.map((process: ActiveProcess) => (
                <div key={process.id} className="p-3 border rounded-lg" data-testid={`process-${process.id}`}>
                  <div className="flex justify-between items-center mb-2">
                    <p className="font-medium">{process.name}</p>
                    <Badge variant="outline" className={getStatusColor(process.status)}>
                      {process.status}
                    </Badge>
                  </div>
                  <div className="space-y-1">
                    <Progress value={process.progress} className="h-2" />
                    <div className="flex justify-between text-xs text-muted-foreground">
                      <span>{process.progress}% complete</span>
                      <span>ETA: {process.eta}</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* System Status */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <Package className="w-5 h-5 mr-2 text-green-600" />
              System Status
            </CardTitle>
            <CardDescription>Production line and equipment status</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {systemMetrics.map((system: SystemMetric, index: number) => (
                <div key={index} className="flex items-center justify-between p-3 border rounded-lg" data-testid={`system-${index}`}>
                  <div className="flex items-center space-x-3">
                    <div className={`w-3 h-3 rounded-full ${
                      system.status === 'operational' ? 'bg-green-500' : 
