// Test script to verify password hashing
const { scrypt, randomBytes, timingSafeEqual } = require("crypto");
const { promisify } = require("util");

const scryptAsync = promisify(scrypt);

async function hashPassword(password) {
  const salt = randomBytes(16).toString("hex");
  const buf = await scryptAsync(password, salt, 64);
  return `${buf.toString("hex")}.${salt}`;
}

async function comparePasswords(supplied, stored) {
  const [hashed, salt] = stored.split(".");
  const hashedBuf = Buffer.from(hashed, "hex");
  const suppliedBuf = await scryptAsync(supplied, salt, 64);
  return timingSafeEqual(hashedBuf, suppliedBuf);
}

async function testPassword() {
  const testPassword = "admin";
  const storedHash = "b5191262d079ecf72a4a4b248f4da5c3b511d5b8680bc5affce26575e23d6c07b87b0fa81498a635227b89c79d626a14b0bff56ff19c55f9fd9d472304d8b672.a302e4ec5badc88c0aa0fa64fce0e018";

  console.log("Testing password:", testPassword);
  console.log("Stored hash:", storedHash);

  // Test if the current stored hash matches "admin"
  const matches = await comparePasswords(testPassword, storedHash);
  console.log("Password 'admin' matches stored hash:", matches);

  // Generate a new hash for "admin" to see what it should look like
  const newHash = await hashPassword(testPassword);
  console.log("New hash for 'admin':", newHash);

  // Test the new hash
  const newMatches = await comparePasswords(testPassword, newHash);
  console.log("Password 'admin' matches new hash:", newMatches);
}

testPassword().catch(console.error);

