/**
 * Security-Specific Threat Detection Service
 * Specialized AI analysis for retail security threats using OpenAI Vision API
 */

import OpenAI from "openai";
import { randomUUID } from "crypto";
import { storage } from "../storage";
import { aiVideoAnalyticsService, type AIDetectionResult, type FrameAnalysisResult, AI_MODELS } from "./videoAnalytics";

// Initialize OpenAI client
const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY,
});

// Threat categories and classifications
export const THREAT_CATEGORIES = {
  THEFT: 'theft',
  VIOLENCE: 'violence', 
  UNAUTHORIZED_ACCESS: 'unauthorized_access',
  WEAPONS: 'weapons',
  SUSPICIOUS_BEHAVIOR: 'suspicious_behavior',
  SAFETY_VIOLATION: 'safety_violation',
  VANDALISM: 'vandalism',
  TRESPASSING: 'trespassing'
} as const;

export const THREAT_SUBCATEGORIES = {
  // Theft subcategories
  SHOPLIFTING: 'shoplifting',
  ROBBERY: 'robbery',
  EMPLOYEE_THEFT: 'employee_theft',
  ORGANIZED_RETAIL_CRIME: 'organized_retail_crime',

  // Violence subcategories
  ASSAULT: 'assault',
  FIGHTING: 'fighting',
  DOMESTIC_VIOLENCE: 'domestic_violence',
  THREAT_GESTURES: 'threat_gestures',

  // Weapon subcategories
  KNIFE: 'knife',
  GUN: 'gun',
  IMPROVISED_WEAPON: 'improvised_weapon',
  CONCEALED_WEAPON: 'concealed_weapon',

  // Behavior subcategories
  LOITERING: 'loitering',
  SURVEILLANCE: 'surveillance',
  SUSPICIOUS_PACKAGE: 'suspicious_package',
  ERRATIC_BEHAVIOR: 'erratic_behavior'
} as const;

export const SEVERITY_LEVELS = {
  INFO: 'info',
  LOW: 'low',
  MEDIUM: 'medium',
  HIGH: 'high',
  CRITICAL: 'critical',
  EMERGENCY: 'emergency'
} as const;

export interface ThreatDetection {
  id: string;
  category: keyof typeof THREAT_CATEGORIES;
  subcategory?: keyof typeof THREAT_SUBCATEGORIES;
  severity: keyof typeof SEVERITY_LEVELS;
  confidence: number; // 0-1
  riskScore: number; // 1-10
  priorityLevel: 'low' | 'normal' | 'high' | 'urgent' | 'immediate';

  // Detection details
  description: string;
  reasoning: string;
  evidenceDescription: string;

  // Location and timing
  boundingBox?: {
    x: number;
    y: number;
    width: number;
    height: number;
  };
  timestamp: number;

  // Response requirements
  immediateResponse: boolean;
  lawEnforcementRequired: boolean;
  emergencyServicesRequired: boolean;
  storeEvacuationRequired: boolean;

  // Additional context
  environmentalFactors?: {
    lighting: 'poor' | 'fair' | 'good' | 'excellent';
    crowdLevel: 'empty' | 'sparse' | 'moderate' | 'dense';
    timeOfDay: 'early_morning' | 'morning' | 'afternoon' | 'evening' | 'night' | 'late_night';
  };

  associatedObjects?: string[];
  involvedPersons?: number;
  weaponsDetected?: string[];
}

export interface ThreatAnalysisConfig {
  focusCategories?: (keyof typeof THREAT_CATEGORIES)[];
  minimumConfidence?: number; // 0-1
  minimumRiskScore?: number; // 1-10
  enableRealTimeAlerts?: boolean;
  customThreatPrompts?: Record<string, string>;
  contextualFactors?: {
    storeType?: 'grocery' | 'electronics' | 'pharmacy' | 'clothing' | 'general';
    operatingHours?: { open: string; close: string };
    highRiskAreas?: string[];
    currentEvents?: string[];
  };
}

export interface ComprehensiveThreatAssessment {
  assessmentId: string;
  storeId: string;
  cameraId: string;
  timestamp: Date;

  detectedThreats: ThreatDetection[];
  overallRiskLevel: keyof typeof SEVERITY_LEVELS;
  recommendedActions: string[];

  analysisMetrics: {
    totalThreats: number;
    highSeverityThreats: number;
    averageConfidence: number;
    averageRiskScore: number;
    processingTime: number;
  };

  contextualAnalysis: {
    sceneDescription: string;
    crowdDynamics: string;
    environmentalRisks: string[];
    temporalFactors: string[];
  };
}

export class ThreatDetectionService {
  private threatClassifications = new Map<string, any>(); // Cache for threat classifications

  constructor() {
    this.loadThreatClassifications();
  }

  /**
   * Load threat classifications from database for enhanced analysis
   */
  private async loadThreatClassifications(): Promise<void> {
    try {
      const classifications = await storage.getThreatClassificationsByStore('default') || [];
      for (const classification of classifications) {
        this.threatClassifications.set(classification.id, classification);
      }
      console.log(`Loaded ${classifications.length} threat classifications`);
    } catch (error) {
      console.warn('Failed to load threat classifications:', error);
    }
  }

  /**
   * Analyze frame specifically for security threats with specialized prompts
   */
  async analyzeThreatFrame(
    imageBuffer: Buffer,
    storeId: string,
    cameraId: string,
    config: ThreatAnalysisConfig = {}
  ): Promise<ComprehensiveThreatAssessment> {
    const assessmentId = randomUUID();
    const startTime = Date.now();
    const timestamp = new Date();

    try {
      // Build specialized threat detection prompt
      const threatPrompt = this.buildThreatDetectionPrompt(config);

      // Convert image to base64
      const base64Image = imageBuffer.toString('base64');

      const response = await openai.chat.completions.create({
        model: AI_MODELS.GPT_4O,
        messages: [
          {
            role: "user",
            content: [
              {
                type: "text",
                text: threatPrompt
              },
              {
                type: "image_url",
                image_url: {
                  url: `data:image/jpeg;base64,${base64Image}`
                }
              }
            ],
          },
        ],
        response_format: { type: "json_object" },
        max_tokens: 3000,
      });

      // Robust JSON parsing with error handling
      let analysis: any = {};
      const responseContent = response.choices[0]?.message?.content;

      if (responseContent) {
        try {
          analysis = JSON.parse(responseContent);
        } catch (parseError) {
          console.warn('Threat detection JSON parsing failed:', parseError);
          console.warn('Raw response content:', responseContent);

          // Attempt to extract JSON from partial response
          const jsonMatch = responseContent.match(/\{[\s\S]*\}/);
          if (jsonMatch) {
            try {
              analysis = JSON.parse(jsonMatch[0]);
            } catch (secondParseError) {
              console.error('Failed to parse extracted JSON:', secondParseError);
              analysis = this.getDefaultThreatAnalysis();
            }
          } else {
            analysis = this.getDefaultThreatAnalysis();
          }
        }
      } else {
        console.warn('Empty response content for threat detection');
        analysis = this.getDefaultThreatAnalysis();
      }

      const processingTime = Date.now() - startTime;

      // Parse threats from AI response
      const detectedThreats = this.parseThreats(analysis, timestamp.getTime());

      // Calculate overall risk assessment
      const overallRiskLevel = this.calculateOverallRisk(detectedThreats);
      const recommendedActions = this.generateRecommendedActions(detectedThreats, analysis);

      const assessment: ComprehensiveThreatAssessment = {
        assessmentId,
        storeId,
        cameraId,
        timestamp,
        detectedThreats,
        overallRiskLevel,
        recommendedActions,
        analysisMetrics: {
          totalThreats: detectedThreats.length,
          highSeverityThreats: detectedThreats.filter(t => 
            t.severity === 'HIGH' || t.severity === 'CRITICAL' || t.severity === 'EMERGENCY'
          ).length,
          averageConfidence: detectedThreats.length > 0 
            ? detectedThreats.reduce((sum, t) => sum + t.confidence, 0) / detectedThreats.length 
            : 0,
          averageRiskScore: detectedThreats.length > 0
            ? detectedThreats.reduce((sum, t) => sum + t.riskScore, 0) / detectedThreats.length
            : 0,
          processingTime
        },
        contextualAnalysis: {
          sceneDescription: analysis.sceneDescription || 'No description available',
          crowdDynamics: analysis.crowdDynamics || 'No crowd analysis available',
          environmentalRisks: analysis.environmentalRisks || [],
          temporalFactors: analysis.temporalFactors || []
        }
      };

      // Store threat assessment and create alerts if necessary
      await this.processThreatAssessment(assessment);

      return assessment;

    } catch (error) {
      console.error('Threat analysis failed:', error);
      throw new Error(`Threat analysis failed: ${error instanceof Error ? error.message : String(error)}`);
    }
  }

  /**
   * Build specialized threat detection prompt
   */
  private buildThreatDetectionPrompt(config: ThreatAnalysisConfig): string {
    const focusCategories = config.focusCategories || Object.values(THREAT_CATEGORIES);
    const minConfidence = config.minimumConfidence || 0.7;
    const contextualInfo = config.contextualFactors ? this.buildContextualInfo(config.contextualFactors) : '';

    return `
You are an expert security threat detection AI analyzing retail security camera footage. Your primary mission is to identify and assess threats to store security, personnel, and customers.

CRITICAL THREAT CATEGORIES TO DETECT:
${focusCategories.map(category => `
- **${category.toUpperCase()}**: ${this.getThreatCategoryDescription(category)}
`).join('')}

ANALYSIS REQUIREMENTS:
1. **THREAT IDENTIFICATION**: Detect and classify all potential security threats
2. **RISK ASSESSMENT**: Evaluate severity and immediate danger level
3. **EVIDENCE ANALYSIS**: Describe visible evidence supporting threat classification
4. **CONTEXTUAL UNDERSTANDING**: Consider environmental and situational factors
5. **ACTIONABLE INTELLIGENCE**: Provide clear, actionable threat information

SECURITY FOCUS AREAS:
- **Theft Prevention**: Shoplifting, concealment, suspicious bag/container usage
- **Violence Detection**: Aggressive behavior, physical altercations, threatening gestures
- **Weapon Identification**: Knives, guns, improvised weapons, concealed weapons
- **Access Control**: Unauthorized area access, after-hours presence, restricted zones
- **Suspicious Behavior**: Loitering, surveillance, case-building, coordinated activity
- **Emergency Situations**: Medical emergencies, safety hazards, crowd control issues

${contextualInfo}

RESPONSE FORMAT - Provide EXACT JSON structure:
{
  "threats": [
    {
      "id": "threat_1",
      "category": "${focusCategories[0]}",
      "subcategory": "specific_threat_type",
      "severity": "low|medium|high|critical|emergency",
      "confidence": 0.85,
      "riskScore": 7,
      "priorityLevel": "low|normal|high|urgent|immediate",
      "description": "Clear, specific description of the threat",
      "reasoning": "Detailed explanation of why this is classified as a threat",
      "evidenceDescription": "Visible evidence supporting this threat assessment",
      "boundingBox": {"x": 100, "y": 50, "width": 200, "height": 300},
      "immediateResponse": true,
      "lawEnforcementRequired": false,
      "emergencyServicesRequired": false,
      "storeEvacuationRequired": false,
      "environmentalFactors": {
        "lighting": "good",
        "crowdLevel": "moderate",
        "timeOfDay": "afternoon"
      },
      "associatedObjects": ["bag", "container"],
      "involvedPersons": 1,
      "weaponsDetected": []
    }
  ],
  "overallRiskAssessment": "low|medium|high|critical|emergency",
  "sceneDescription": "Comprehensive description of the overall scene",
  "crowdDynamics": "Analysis of crowd behavior and movement patterns",
  "environmentalRisks": ["poor lighting", "blind spots"],
  "temporalFactors": ["after hours", "peak shopping time"],
  "recommendedActions": [
    "Immediate action 1",
    "Follow-up action 2"
  ],
  "confidenceLevel": 0.85,
  "analysisComplete": true
}

CRITICAL GUIDELINES:
- Only flag actual threats with confidence >= ${minConfidence}
- Provide specific, actionable descriptions
- Consider false positive implications
- Focus on immediate security concerns
- Prioritize customer and staff safety
- Account for normal retail behavior vs. suspicious activity

Be thorough but precise. Err on the side of caution for high-severity threats.
`;
  }

  /**
   * Parse threat detections from AI response
   */
  private parseThreats(analysis: any, timestamp: number): ThreatDetection[] {
    const threats: ThreatDetection[] = [];

    if (!analysis.threats || !Array.isArray(analysis.threats)) {
      return threats;
    }

    for (const threat of analysis.threats) {
      const detection: ThreatDetection = {
        id: threat.id || randomUUID(),
        category: threat.category || 'suspicious_behavior',
        subcategory: threat.subcategory,
        severity: threat.severity || 'low',
        confidence: Math.min(Math.max(threat.confidence || 0.5, 0), 1),
        riskScore: Math.min(Math.max(threat.riskScore || 1, 1), 10),
        priorityLevel: threat.priorityLevel || 'normal',
        description: threat.description || 'No description provided',
        reasoning: threat.reasoning || 'No reasoning provided',
        evidenceDescription: threat.evidenceDescription || 'No evidence description',
        boundingBox: threat.boundingBox,
        timestamp,
        immediateResponse: threat.immediateResponse || false,
        lawEnforcementRequired: threat.lawEnforcementRequired || false,
        emergencyServicesRequired: threat.emergencyServicesRequired || false,
        storeEvacuationRequired: threat.storeEvacuationRequired || false,
        environmentalFactors: threat.environmentalFactors,
        associatedObjects: threat.associatedObjects || [],
        involvedPersons: threat.involvedPersons || 1,
        weaponsDetected: threat.weaponsDetected || []
      };

      threats.push(detection);
    }

    return threats;
  }

  /**
   * Calculate overall risk level from detected threats
   */
  private calculateOverallRisk(threats: ThreatDetection[]): keyof typeof SEVERITY_LEVELS {
    if (threats.length === 0) return 'INFO';

    const severityScores = {
      'INFO': 0,
      'LOW': 1,
      'MEDIUM': 2, 
      'HIGH': 3,
      'CRITICAL': 4,
      'EMERGENCY': 5
    };

    let maxSeverity = 0;
    let avgRiskScore = 0;

    for (const threat of threats) {
      const severityScore = severityScores[threat.severity] || 0;
      maxSeverity = Math.max(maxSeverity, severityScore);
      avgRiskScore += threat.riskScore;
    }

    avgRiskScore /= threats.length;

    // Determine overall risk based on highest severity and average risk score
    if (maxSeverity >= 5 || avgRiskScore >= 9) return 'EMERGENCY';
    if (maxSeverity >= 4 || avgRiskScore >= 8) return 'CRITICAL';
    if (maxSeverity >= 3 || avgRiskScore >= 6) return 'HIGH';
    if (maxSeverity >= 2 || avgRiskScore >= 4) return 'MEDIUM';
    if (maxSeverity >= 1 || avgRiskScore >= 2) return 'LOW';

    return 'INFO';
  }

  /**
   * Generate recommended actions based on threats
   */
  private generateRecommendedActions(threats: ThreatDetection[], analysis: any): string[] {
    const actions: string[] = [];

    for (const threat of threats) {
      if (threat.immediateResponse) {
        actions.push(`IMMEDIATE: Respond to ${threat.category} threat - ${threat.description}`);
      }

      if (threat.lawEnforcementRequired) {
        actions.push(`Contact law enforcement regarding ${threat.category} incident`);
      }

      if (threat.emergencyServicesRequired) {
        actions.push(`Call emergency services for ${threat.category} situation`);
      }

      if (threat.storeEvacuationRequired) {
        actions.push(`EVACUATE STORE: ${threat.category} requires immediate evacuation`);
      }

      if (threat.weaponsDetected && threat.weaponsDetected.length > 0) {
        actions.push(`Weapon detected: ${threat.weaponsDetected.join(', ')} - Exercise extreme caution`);
      }
    }

    // Add general recommendations from analysis
    if (analysis.recommendedActions && Array.isArray(analysis.recommendedActions)) {
      actions.push(...analysis.recommendedActions);
    }

    return Array.from(new Set(actions)); // Remove duplicates
  }

  /**
   * Process threat assessment and create alerts/notifications
   */
  private async processThreatAssessment(assessment: ComprehensiveThreatAssessment): Promise<void> {
    try {
      // Create alerts for high-priority threats
      for (const threat of assessment.detectedThreats) {
        if (threat.severity === 'HIGH' || threat.severity === 'CRITICAL' || threat.severity === 'EMERGENCY') {
          await this.createThreatAlert(threat, assessment);
        }

        // Store individual threat detection
        await this.storeThreatDetection(threat, assessment);
      }

      // Store overall assessment
      await this.storeThreatAssessment(assessment);

      console.log(`Processed threat assessment ${assessment.assessmentId} with ${assessment.detectedThreats.length} threats`);

    } catch (error) {
      console.error('Failed to process threat assessment:', error);
    }
  }

  /**
   * Create alert for detected threat
   */
  private async createThreatAlert(threat: ThreatDetection, assessment: ComprehensiveThreatAssessment): Promise<void> {
    try {
      const alertTitle = `${threat.severity.toUpperCase()} THREAT: ${threat.category.replace('_', ' ').toUpperCase()}`;
      const alertMessage = `${threat.description}\n\nEvidence: ${threat.evidenceDescription}\n\nRisk Score: ${threat.riskScore}/10\nConfidence: ${Math.round(threat.confidence * 100)}%`;

      await storage.createAlert({
        storeId: assessment.storeId,
        cameraId: assessment.cameraId,
        type: threat.category as any,
        severity: threat.severity === 'EMERGENCY' ? 'CRITICAL' : threat.severity as any,
        title: alertTitle,
        message: alertMessage,
        isRead: false,
        isActive: true,
        metadata: {
          assessmentId: assessment.assessmentId,
          riskScore: threat.riskScore,
          confidence: threat.confidence,
          recommendedActions: assessment.recommendedActions,
          weaponsDetected: threat.weaponsDetected,
          immediateResponse: threat.immediateResponse,
          lawEnforcementRequired: threat.lawEnforcementRequired
        }
      });

    } catch (error) {
      console.error('Failed to create threat alert:', error);
    }
  }

  /**
   * Store threat detection in database
   */
  private async storeThreatDetection(threat: ThreatDetection, assessment: ComprehensiveThreatAssessment): Promise<void> {
    try {
      await storage.createAiDetection({
        storeId: assessment.storeId,
        cameraId: assessment.cameraId,
        detectionType: 'threat',
        threatType: threat.category as any,
        behaviorType: threat.subcategory as any,
        confidence: threat.confidence,
        boundingBox: threat.boundingBox,
        modelName: AI_MODELS.GPT_4O,
        modelVersion: '1.0',
        processingTime: assessment.analysisMetrics.processingTime,
        frameTimestamp: new Date(threat.timestamp),
        metadata: {
          riskScore: threat.riskScore,
          priorityLevel: threat.priorityLevel,
          description: threat.description,
          reasoning: threat.reasoning,
          evidenceDescription: threat.evidenceDescription,
          immediateResponse: threat.immediateResponse,
          lawEnforcementRequired: threat.lawEnforcementRequired,
          emergencyServicesRequired: threat.emergencyServicesRequired,
          weaponsDetected: threat.weaponsDetected,
          associatedObjects: threat.associatedObjects,
          environmentalFactors: threat.environmentalFactors
        }
      });

    } catch (error) {
      console.error('Failed to store threat detection:', error);
    }
  }

  /**
   * Store threat assessment in database
   */
  private async storeThreatAssessment(assessment: ComprehensiveThreatAssessment): Promise<void> {
    // Implementation would store in video_analytics table or create new threat_assessments table
    // For now, we'll use the video_analytics table
    try {
      await storage.createVideoAnalysis({
        storeId: assessment.storeId,
        cameraId: assessment.cameraId,
        videoFilePath: `threat_analysis_${assessment.assessmentId}`,
        analysisStatus: 'completed',
        detectedFaces: [],
        matchedOffenders: [],
        confidenceScores: { average: assessment.analysisMetrics.averageConfidence },
        modelsUsed: [{
          name: AI_MODELS.GPT_4O,
          version: '1.0',
          purpose: 'threat_detection'
        }],
        processingTime: assessment.analysisMetrics.processingTime,
        analyticsResults: {
          overallRiskLevel: assessment.overallRiskLevel,
          averageConfidence: assessment.analysisMetrics.averageConfidence,
          averageRiskScore: assessment.analysisMetrics.averageRiskScore,
          recommendedActions: assessment.recommendedActions,
          contextualAnalysis: assessment.contextualAnalysis
        }
      });

    } catch (error) {
      console.error('Failed to store threat assessment:', error);
    }
  }

  /**
   * Get threat category description for prompts
   */
  private getThreatCategoryDescription(category: string): string {
    const descriptions = {
      theft: 'Shoplifting, concealment, organized retail crime, employee theft',
      violence: 'Physical altercations, assault, threatening behavior, domestic violence',
      unauthorized_access: 'Restricted area breach, after-hours presence, trespassing',
      weapons: 'Knives, guns, improvised weapons, concealed weapons',
      suspicious_behavior: 'Loitering, surveillance, case-building, erratic behavior',
      safety_violation: 'Safety hazards, emergency situations, crowd control issues',
      vandalism: 'Property damage, graffiti, destruction of merchandise',
      trespassing: 'Unauthorized presence, banned individuals, restricted access'
    };

    return descriptions[category as keyof typeof descriptions] || 'Security threat requiring assessment';
  }

  /**
   * Build contextual information for enhanced analysis
   */
  private buildContextualInfo(factors: NonNullable<ThreatAnalysisConfig['contextualFactors']>): string {
    let context = '\nCONTEXTUAL FACTORS:\n';

    if (factors.storeType) {
      context += `- Store Type: ${factors.storeType} (adjust threat assessment accordingly)\n`;
    }

    if (factors.operatingHours) {
      context += `- Operating Hours: ${factors.operatingHours.open} - ${factors.operatingHours.close}\n`;
    }

    if (factors.highRiskAreas && factors.highRiskAreas.length > 0) {
      context += `- High-Risk Areas: ${factors.highRiskAreas.join(', ')}\n`;
    }

    if (factors.currentEvents && factors.currentEvents.length > 0) {
      context += `- Current Events/Alerts: ${factors.currentEvents.join(', ')}\n`;
    }

    return context;
  }

  /**
   * Verify threat detection (for manual review/feedback)
   */
  async verifyThreatDetection(
    detectionId: string,
    isValid: boolean,
    feedback: string,
    verifiedBy: string
  ): Promise<void> {
    try {
      await storage.updateAiDetection(detectionId, {
        isVerified: true,
        verifiedBy,
        verifiedAt: new Date(),
        isFalsePositive: !isValid,
        notes: feedback
      });

      console.log(`Threat detection ${detectionId} verified as ${isValid ? 'valid' : 'false positive'}`);

    } catch (error) {
      console.error('Failed to verify threat detection:', error);
      throw error;
    }
  }

  /**
   * Get default threat analysis structure for error cases
   */
  private getDefaultThreatAnalysis(): any {
    return {
      detectedThreats: [],
      overallRiskLevel: 'low',
      sceneDescription: 'Analysis failed - using default values',
      crowdDynamics: 'Unknown',
      environmentalRisks: [],
      temporalFactors: [],
      totalThreats: 0,
      highSeverityThreats: 0,
      averageConfidence: 0,
      averageRiskScore: 0
    };
  }
}

export const threatDetectionService = new ThreatDetectionService();

server/ai/videoAnalytics.ts.content:
/**
 * Comprehensive AI Video Analytics Service
 * Powered by OpenAI Vision API for threat detection and behavior analysis
 */

import OpenAI from "openai";
import { promises as fs } from "fs";
import path from "path";
import { randomUUID } from "crypto";
import { storage } from "../storage";
import { DetectionResult, DetectionBoundingBox, ThreatSeverity, InsertBehaviorEvent } from "../../shared/schema";
import { baselineBuilder } from "../behavioral/baselineBuilder";
import { anomalyDetector } from "../behavioral/anomalyDetector";
import { behavioralAlertEngine } from "../behavioral/behavioralAlerts";
import { FacialRecognitionService } from "./facialRecognition";
import { CONSENT_TYPES } from "../consent-middleware";

// Initialize OpenAI client
const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY,
});

// Supported OpenAI Vision models
export const AI_MODELS = {
  GPT_4_VISION: "gpt-4-vision-preview",
  GPT_4O: "gpt-4o", // Current production model with vision
  GPT_4_TURBO: "gpt-4-turbo", 
} as const;

export type AIModel = typeof AI_MODELS[keyof typeof AI_MODELS];

// AI Detection types
export interface AIDetectionResult {
  id: string;
  detectionType: 'person' | 'object' | 'behavior' | 'threat' | 'anomaly' | 'face';
  objectClass?: string;
  threatType?: 'theft' | 'violence' | 'unauthorized_access' | 'weapons' | 'suspicious_behavior';
  behaviorType?: 'suspicious' | 'aggressive' | 'normal' | 'panic' | 'loitering';
  confidence: number; // 0-1 scale
  boundingBox?: {
    x: number;
    y: number;
    width: number;
    height: number;
    normalized?: boolean;
  };
  keyPoints?: Array<{
    x: number;
    y: number;
    confidence: number;
    label?: string;
  }>;
  description: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
  frameTimestamp: number;
  processingTime: number;
  // Facial recognition specific fields
  faceData?: {
    personId?: string;
    templateId?: string;
    watchlistMatch?: boolean;
    matchConfidence?: number;
    facialFeatures?: string; // Encrypted template data
    consentVerified?: boolean;
    demographicEstimates?: {
      ageRange?: string;
      gender?: string;
      ethnicity?: string;
    };
  };
}

export interface FrameAnalysisResult {
  frameId: string;
  frameNumber: number;
  timestamp: number;
  detections: AIDetectionResult[];
  qualityScore: number;
  lightingConditions: 'poor' | 'fair' | 'good' | 'excellent';
  motionLevel: 'low' | 'medium' | 'high';
  crowdDensity: 'empty' | 'sparse' | 'moderate' | 'dense';
  modelUsed: AIModel;
  processingTime: number;
}

export interface VideoAnalysisConfig {
  model?: AIModel;
  confidenceThreshold?: number;
  frameInterval?: number; // seconds between analyzed frames
  enableThreatDetection?: boolean;
  enableBehaviorAnalysis?: boolean;
  enableObjectDetection?: boolean;
  enableFacialRecognition?: boolean; // NEW: Facial recognition analysis
  customPrompt?: string;
  // Facial recognition specific config
  facialRecognition?: {
    enableWatchlistMatching?: boolean;
    requireExplicitConsent?: boolean;
    faceConfidenceThreshold?: number;
    matchConfidenceThreshold?: number;
    enableDemographicEstimates?: boolean;
    privacyMode?: 'strict' | 'balanced' | 'minimal'; // Privacy compliance level
  };
}

export interface ComprehensiveVideoAnalysis {
  analysisId: string;
  videoPath: string;
  storeId: string;
  cameraId: string;

  // Analysis results
  frames: FrameAnalysisResult[];
  totalDetections: number;
  threatDetections: number;
  suspiciousActivities: AIDetectionResult[];

  // Summary metrics
  averageConfidence: number;
  qualityScore: number;
  processingDuration: number;
  frameRate: number;
  resolution: string;

  // Model and configuration
  modelUsed: AIModel;
  config: VideoAnalysisConfig;

  // Status
  status: 'pending' | 'processing' | 'completed' | 'failed';
  error?: string;
  createdAt: Date;
  completedAt?: Date;
}

export class AIVideoAnalyticsService {
  private uploadDir = path.join(process.cwd(), 'uploads');
  private frameCache = new Map<string, string>(); // frame ID -> base64 cache
  private analysisCache = new Map<string, FrameAnalysisResult>(); // frame hash -> analysis cache
  private facialRecognitionService: FacialRecognitionService;

  constructor() {
    this.ensureDirectories();
    this.facialRecognitionService = new FacialRecognitionService();
  }

  private async ensureDirectories() {
    const dirs = [
      this.uploadDir,
      path.join(this.uploadDir, 'frames'),
      path.join(this.uploadDir, 'faces'),
      path.join(this.uploadDir, 'clips'),
      path.join(this.uploadDir, 'thumbnails')
    ];

    for (const dir of dirs) {
      try {
        await fs.mkdir(dir, { recursive: true });
      } catch (error) {
        console.error(`Failed to create directory ${dir}:`, error);
      }
    }
  }

  /**
   * Analyze a single frame with comprehensive AI detection
   */
  async analyzeFrame(
    framePath: string, 
    frameNumber: number, 
    timestamp: number,
    cameraId: string,
    storeId: string,
    config: VideoAnalysisConfig = {}
  ): Promise<FrameAnalysisResult> {
    const frameId = `frame_${frameNumber}_${Date.now()}`;
    const startTime = Date.now();

    try {
      // Check cache first
      const frameHash = await this.getFrameHash(framePath);
      if (this.analysisCache.has(frameHash)) {
        console.log(`Using cached analysis for frame ${frameNumber}`);
        return this.analysisCache.get(frameHash)!;
      }

      const base64Frame = await this.frameToBase64(framePath);
      const model = config.model || AI_MODELS.GPT_4O;

      // Comprehensive security analysis prompt
      const analysisPrompt = this.buildAnalysisPrompt(config);

      // OpenAI call with AbortController and circuit breaker protection
      const response = await this.makeOpenAICallWithCircuitBreaker(model, analysisPrompt, base64Frame, frameNumber);

      // Robust JSON parsing with error handling
      let analysis: any = {};
      const responseContent = response.choices[0]?.message?.content;

      if (responseContent) {
        try {
          analysis = JSON.parse(responseContent);
        } catch (parseError) {
          console.warn(`JSON parsing failed for frame ${frameNumber}:`, parseError);
          console.warn('Raw response content:', responseContent);

          // Attempt to extract JSON from partial response
          const jsonMatch = responseContent.match(/\{[\s\S]*\}/);
          if (jsonMatch) {
            try {
              analysis = JSON.parse(jsonMatch[0]);
            } catch (secondParseError) {
              console.error('Failed to parse extracted JSON:', secondParseError);
              analysis = this.getDefaultAnalysis();
            }
          } else {
            analysis = this.getDefaultAnalysis();
          }
        }
      } else {
        console.warn(`Empty response content for frame ${frameNumber}`);
        analysis = this.getDefaultAnalysis();
      }

      const processingTime = Date.now() - startTime;

      // Parse AI response into structured detections with validation
      const detections = this.parseDetections(analysis, timestamp, processingTime);

      // FACIAL RECOGNITION INTEGRATION - Privacy-Compliant Processing
      // Add facial recognition analysis alongside existing threat detection
      let facialDetections: AIDetectionResult[] = [];
      if (config.enableFacialRecognition) {
        try {
          console.log(`[FACIAL_RECOGNITION] Processing frame ${frameNumber} for facial recognition`);
          facialDetections = await this.analyzeFacialRecognition(
            base64Frame,
            storeId,
            cameraId,
            timestamp,
            config
          );
          console.log(`[FACIAL_RECOGNITION] Frame ${frameNumber}: ${facialDetections.length} faces detected`);
        } catch (facialError) {
          console.error(`[FACIAL_RECOGNITION] Frame ${frameNumber} failed:`, facialError);
          // Don't fail entire analysis due to facial recognition errors
        }
      }

      // Combine all detections (threat detection + facial recognition)
      const allDetections = [...detections, ...facialDetections];

      const result: FrameAnalysisResult = {
        frameId,
        frameNumber,
        timestamp,
        detections: allDetections, // Combined detections including facial recognition
        qualityScore: analysis.qualityScore || 0.8,
        lightingConditions: analysis.lightingConditions || 'good',
        motionLevel: analysis.motionLevel || 'low',
        crowdDensity: analysis.crowdDensity || 'sparse',
        modelUsed: model,
        processingTime
      };

      // Cache the result
      this.analysisCache.set(frameHash, result);

      // BEHAVIORAL PATTERN LEARNING INTEGRATION
      // Extract and emit behavioral events for pattern learning
      if (config.enableBehaviorAnalysis !== false) { // Default to enabled
        try {
          await this.processBehavioralPatterns(result, framePath, { storeId, cameraId });
        } catch (behaviorError) {
          console.error("Behavioral pattern processing failed:", behaviorError);
          // Don't fail entire analysis due to behavioral processing errors
        }
      }

      return result;

    } catch (error) {
      console.error(`Frame analysis failed for frame ${frameNumber}:`, error);

      // Return empty result on error
      return {
        frameId,
        frameNumber,
        timestamp,
        detections: [],
        qualityScore: 0,
        lightingConditions: 'poor',
        motionLevel: 'low',
        crowdDensity: 'empty',
        modelUsed: config.model || AI_MODELS.GPT_4O,
        processingTime: Date.now() - startTime
      };
    }
  }

  /**
   * Make OpenAI call with circuit breaker protection, AbortController timeout, and retries
   */
  private async makeOpenAICallWithCircuitBreaker(
    model: AIModel, 
    prompt: string, 
    base64Frame: string, 
    frameNumber: number,
    maxRetries: number = 2
  ): Promise<any> {
    const TIMEOUT_MS = 15000; // 15 seconds as per security requirements

    for (let attempt = 1; attempt <= maxRetries; attempt++) {
      const abortController = new AbortController();
      const timeoutId = setTimeout(() => {
        abortController.abort();
      }, TIMEOUT_MS);

      try {
        const startTime = Date.now();

        // Log attempt start
        console.log(`[OPENAI] Starting analysis attempt ${attempt}/${maxRetries} for frame ${frameNumber}`);

        const response = await openai.chat.completions.create({
          model,
          messages: [
            {
              role: "user",
              content: [
                {
                  type: "text",
                  text: prompt
                },
                {
                  type: "image_url",
                  image_url: {
                    url: `data:image/jpeg;base64,${base64Frame}`
                  }
                }
              ],
            },
          ],
          response_format: { type: "json_object" },
          max_tokens: 2000,
        }, {
          signal: abortController.signal
        });

        clearTimeout(timeoutId);
        const processingTime = Date.now() - startTime;

        // Log successful call
        console.log(`[OPENAI] Analysis completed successfully in ${processingTime}ms for frame ${frameNumber}`);

        return response;

      } catch (error: any) {
        clearTimeout(timeoutId);
        const processingTime = Date.now() - Date.now();

        // Determine error type for structured logging
        const isTimeout = error.name === 'AbortError' || error.message?.includes('timeout');
        const isRateLimit = error.status === 429;

        // Log structured security event
        const logData = {
          attempt,
          maxRetries,
          frameNumber,
          model,
          processingTime,
          errorType: isTimeout ? 'TIMEOUT' : isRateLimit ? 'RATE_LIMIT' : 'API_ERROR',
          errorMessage: error.message,
          errorStatus: error.status,
        };

        if (isTimeout) {
          console.error(`[SECURITY] OpenAI API timeout on attempt ${attempt}/${maxRetries}:`, logData);
        } else if (isRateLimit) {
          console.error(`[SECURITY] OpenAI rate limit exceeded on attempt ${attempt}/${maxRetries}:`, logData);
        } else {
          console.error(`[SECURITY] OpenAI API error on attempt ${attempt}/${maxRetries}:`, logData);
        }

        // If this is the last attempt, throw the error
        if (attempt === maxRetries) {
          console.error(`[SECURITY] All ${maxRetries} attempts failed for frame ${frameNumber}. Circuit breaker activated.`);
          throw new Error(`OpenAI analysis failed after ${maxRetries} attempts: ${error.message}`);
        }

        // Calculate exponential backoff delay
        const backoffDelay = Math.min(1000 * Math.pow(2, attempt - 1), 5000); // Max 5 seconds
        console.log(`[OPENAI] Retrying in ${backoffDelay}ms (exponential backoff)...`);

        await new Promise(resolve => setTimeout(resolve, backoffDelay));
      }
    }

    // This should never be reached, but included for type safety
    throw new Error('OpenAI call failed unexpectedly');
  }

  /**
   * Build comprehensive analysis prompt based on configuration
   */
  private buildAnalysisPrompt(config: VideoAnalysisConfig): string {
    if (config.customPrompt) {
      return config.customPrompt;
    }

    let prompt = `
Analyze this security camera frame for comprehensive threat detection and behavior analysis.

DETECTION REQUIREMENTS:
1. **THREAT DETECTION**: Identify theft, violence, unauthorized access, weapons, suspicious behavior
2. **BEHAVIOR ANALYSIS**: Analyze human behavior patterns, movements, interactions
3. **OBJECT DETECTION**: Detect relevant objects (bags, packages, weapons, tools)
4. **CROWD ANALYSIS**: Assess crowd density and movement patterns
5. **ENVIRONMENTAL ASSESSMENT**: Evaluate lighting, visibility, and scene conditions

CRITICAL SECURITY FOCUS:
- Shoplifting/theft indicators (concealment, furtive behavior)
- Violence or aggressive behavior
- Unauthorized access to restricted areas
- Weapon detection (knives, guns, tools used as weapons)
- Suspicious loitering or surveillance behavior
- Emergency situations (medical, safety hazards)

Respond with JSON in this EXACT format:
{
  "detections": [
    {
      "id": "det_1",
      "detectionType": "threat|person|object|behavior|anomaly",
      "objectClass": "weapon|bag|person|vehicle|tool",
      "threatType": "theft|violence|unauthorized_access|weapons|suspicious_behavior",
      "behaviorType": "suspicious|aggressive|normal|panic|loitering",
      "confidence": 0.85,
      "boundingBox": {"x": 100, "y": 50, "width": 80, "height": 100, "normalized": false},
      "keyPoints": [{"x": 120, "y": 60, "confidence": 0.9, "label": "head"}],
      "description": "Person concealing item in jacket",
      "severity": "high",
      "reasoning": "Furtive movement and concealment behavior indicates potential theft"
    }
  ],
  "qualityScore": 0.85,
  "lightingConditions": "good|fair|poor|excellent",
  "motionLevel": "low|medium|high",
  "crowdDensity": "empty|sparse|moderate|dense",
  "sceneDescription": "Brief description of overall scene",
  "riskAssessment": "overall risk level and key concerns"
}
`;

    // Add specific focus areas based on config
    if (config.enableThreatDetection !== false) {
      prompt += "\n\nFOCUS HEAVILY on theft indicators, weapons, and threatening behavior.";
    }

    if (config.enableBehaviorAnalysis !== false) {
      prompt += "\n\nAnalyze human behavior patterns in detail.";
    }

    if (config.enableObjectDetection !== false) {
      prompt += "\n\nDetect all relevant objects, especially potential weapons or theft tools.";
    }

    return prompt;
  }

  /**
   * Analyze facial recognition in a frame with privacy controls
   */
  private async analyzeFacialRecognition(
    frameBase64: string,
    storeId: string,
    cameraId: string,
    frameTimestamp: number,
    config: VideoAnalysisConfig,
    userId?: string
  ): Promise<AIDetectionResult[]> {
    const facialDetections: AIDetectionResult[] = [];

    // Check if facial recognition is enabled
    if (!config.enableFacialRecognition) {
      return facialDetections;
    }

    const facialConfig = config.facialRecognition || {};
    const startTime = Date.now();

    try {
      // Verify consent for facial recognition processing
      const consentVerified = await this.verifyFacialRecognitionConsent(
        storeId, 
        facialConfig.requireExplicitConsent !== false
      );

      if (!consentVerified && facialConfig.privacyMode === 'strict') {
        console.log('Facial recognition skipped - no consent verified');
        return facialDetections;
      }

      // Analyze image for faces using facial recognition service
      const faceAnalysisResult = await this.facialRecognitionService.analyzeImageForFaces(frameBase64, {
        storeId,
        cameraId,
        userId: userId || 'video-analytics',
        enableWatchlistMatching: facialConfig.enableWatchlistMatching !== false,
        confidenceThreshold: facialConfig.faceConfidenceThreshold || 0.8,
        enableDemographicEstimates: facialConfig.enableDemographicEstimates === true
      });

      const processingTime = Date.now() - startTime;

      // Process detected faces
      for (const detectedFace of faceAnalysisResult.detectedFaces || []) {
        const detection: AIDetectionResult = {
          id: randomUUID(),
          detectionType: 'face',
          objectClass: 'person',
          confidence: detectedFace.confidence || 0.0,
          boundingBox: detectedFace.boundingBox,
          keyPoints: detectedFace.keyPoints || [],
          description: `Face detected ${detectedFace.watchlistMatch ? '(WATCHLIST MATCH)' : ''}`,
          severity: detectedFace.watchlistMatch ? 'high' : 'low',
          frameTimestamp,
          processingTime,
          faceData: {
            personId: detectedFace.personId,
            templateId: detectedFace.templateId,
            watchlistMatch: detectedFace.watchlistMatch || false,
            matchConfidence: detectedFace.matchConfidence,
            facialFeatures: '[ENCRYPTED]', // Never expose raw template data
            consentVerified,
            demographicEstimates: detectedFace.demographicEstimates
          }
        };

        facialDetections.push(detection);

        // Generate facial recognition event for audit trail
        if (detectedFace.watchlistMatch || facialConfig.privacyMode !== 'minimal') {
          await this.createFacialRecognitionEvent({
            storeId,
            cameraId,
            frameTimestamp,
            detection,
            processingTimeMs: processingTime
          });
        }
      }

      // Process watchlist matches for real-time alerts
      const watchlistMatches = faceAnalysisResult.watchlistMatches || [];
      for (const match of watchlistMatches) {
        await this.handleWatchlistMatch(match, storeId, cameraId, frameTimestamp);
      }

      console.log(`Facial recognition analysis: ${facialDetections.length} faces detected, ${watchlistMatches.length} watchlist matches`);

    } catch (error) {
      console.error('Facial recognition analysis failed:', error);

      // Create audit log for failed analysis
      await storage.logAdvancedFeatureAudit({
        userId: userId || 'video-analytics',
        storeId,
        featureType: 'facial_recognition',
        action: 'analyze_frame',
        outcome: 'error',
        resourceType: 'video_frame',
        details: {
          error: error instanceof Error ? error.message : 'Unknown error',
          frameTimestamp,
          cameraId
        },
        timestamp: new Date()
      });
    }

    return facialDetections;
  }

  /**
   * Verify facial recognition consent for the store
   */
  private async verifyFacialRecognitionConsent(
    storeId: string, 
    requireExplicit: boolean = true
  ): Promise<boolean> {
    try {
      // Check if there's a valid consent for facial recognition
      const consent = await storage.getConsentPreference(
        storeId, 
        CONSENT_TYPES.FACIAL_RECOGNITION,
        'employee' // Default to employee consent for video analytics
      );

      if (!consent) {
        return !requireExplicit; // Allow if not requiring explicit consent
      }

      // Check if consent is active and not withdrawn
      const isValid = consent.consentGiven && 
                     !consent.withdrawnDate && 
                     (!consent.expiryDate || consent.expiryDate > new Date());

      return isValid;
    } catch (error) {
      console.error('Consent verification failed:', error);
      return false; // Fail secure - deny if cannot verify
    }
  }

  /**
   * Create facial recognition event for audit trail
   */
  private async createFacialRecognitionEvent(eventData: {
    storeId: string;
    cameraId: string;
    frameTimestamp: number;
    detection: AIDetectionResult;
    processingTimeMs: number;
  }): Promise<void> {
    try {
      await storage.createFacialRecognitionEvent({
        storeId: eventData.storeId,
        cameraId: eventData.cameraId,
        detectionTimestamp: new Date(eventData.frameTimestamp),
        faceAttributes: {
          confidence: eventData.detection.confidence,
          boundingBox: eventData.detection.boundingBox,
          watchlistMatch: eventData.detection.faceData?.watchlistMatch || false,
          personId: eventData.detection.faceData?.personId,
          templateId: eventData.detection.faceData?.templateId
        },
        matchConfidence: eventData.detection.faceData?.matchConfidence || 0,
        processingTimeMs: eventData.processingTimeMs,
        consentVerified: eventData.detection.faceData?.consentVerified || false
      });
    } catch (error) {
      console.error('Failed to create facial recognition event:', error);
    }
  }

  /**
   * Handle watchlist match for real-time alerts
   */
  private async handleWatchlistMatch(
    match: any, 
    storeId: string, 
    cameraId: string, 
    frameTimestamp: number
  ): Promise<void> {
    try {
      // Create high-priority alert for watchlist match
      const alert = await storage.createAlert({
        storeId,
        type: 'WATCHLIST_MATCH',
        severity: 'HIGH',
        title: 'Watchlist Individual Detected',
        description: `Person of interest detected on camera ${cameraId}. Match confidence: ${match.confidence?.toFixed(2) || 'N/A'}`,
        cameraId,
        timestamp: new Date(frameTimestamp),
        acknowledgedBy: null,
        acknowledgedAt: null,
        actionRequired: true,
        metadata: {
          personId: match.personId,
          matchConfidence: match.confidence,
          watchlistType: match.watchlistType,
          riskLevel: match.riskLevel
        }
      });

      // Log the watchlist match event
      await storage.logAdvancedFeatureAudit({
        userId: 'video-analytics',
        storeId,
        featureType: 'facial_recognition',
        action: 'watchlist_match',
        outcome: 'success',
        resourceType: 'watchlist_entry',
        resourceId: match.watchlistEntryId,
        details: {
          alertId: alert.id,
          personId: match.personId,
          matchConfidence: match.confidence,
          cameraId,
          frameTimestamp
        },
        timestamp: new Date()
      });

      console.log(`Watchlist match alert created: ${alert.id} for person ${match.personId}`);
    } catch (error) {
      console.error('Failed to handle watchlist match:', error);
    }
  }

  /**
   * Parse OpenAI response into structured detection results
   */
  private parseDetections(
    analysis: any, 
    frameTimestamp: number, 
    processingTime: number
  ): AIDetectionResult[] {
    const detections: AIDetectionResult[] = [];

    if (!analysis.detections || !Array.isArray(analysis.detections)) {
      return detections;
    }

    for (const det of analysis.detections) {
      const detection: AIDetectionResult = {
        id: det.id || randomUUID(),
        detectionType: det.detectionType || 'object',
        objectClass: det.objectClass,
        threatType: det.threatType,
        behaviorType: det.behaviorType,
        confidence: Math.min(Math.max(det.confidence || 0.5, 0), 1),
        boundingBox: det.boundingBox,
        keyPoints: det.keyPoints || [],
        description: det.description || 'No description',
        severity: det.severity || 'low',
        frameTimestamp,
        processingTime
      };

      detections.push(detection);
    }

    return detections;
  }

  /**
   * Analyze complete video with comprehensive AI processing
   */
  async analyzeVideo(
    videoPath: string,
    storeId: string,
    cameraId: string,
    config: VideoAnalysisConfig = {}
  ): Promise<ComprehensiveVideoAnalysis> {
    const analysisId = randomUUID();
    const startTime = Date.now();

    const analysis: ComprehensiveVideoAnalysis = {
      analysisId,
      videoPath,
      storeId,
      cameraId,
      frames: [],
      totalDetections: 0,
      threatDetections: 0,
      suspiciousActivities: [],
      averageConfidence: 0,
      qualityScore: 0,
      processingDuration: 0,
      frameRate: 30,
      resolution: "1920x1080",
      modelUsed: config.model || AI_MODELS.GPT_4O,
      config,
      status: 'processing',
      createdAt: new Date()
    };

    try {
      // Extract frames for analysis
      const frameInterval = config.frameInterval || 2; // Every 2 seconds
      const frames = await this.extractFrames(videoPath, frameInterval);

      console.log(`Analyzing ${frames.length} frames from video`);

      // Analyze each frame
      for (let i = 0; i < frames.length; i++) {
        const timestamp = i * frameInterval;
        const frameAnalysis = await this.analyzeFrame(frames[i], i, timestamp, cameraId, storeId, config);

        analysis.frames.push(frameAnalysis);
        analysis.totalDetections += frameAnalysis.detections.length;

        // Count threat detections
        const threats = frameAnalysis.detections.filter(d => 
          d.detectionType === 'threat' || 
          d.severity === 'high' || 
          d.severity === 'critical'
        );
        analysis.threatDetections += threats.length;

        // Collect suspicious activities
        const suspicious = frameAnalysis.detections.filter(d =>
          d.behaviorType === 'suspicious' || 
          d.threatType || 
          d.severity === 'medium' || 
          d.severity === 'high' || 
          d.severity === 'critical'
        );
        analysis.suspiciousActivities.push(...suspicious);

        console.log(`Frame ${i + 1}/${frames.length}: ${frameAnalysis.detections.length} detections, ${threats.length} threats`);
      }

      // Calculate summary metrics
      const allDetections = analysis.frames.flatMap(f => f.detections);
      analysis.averageConfidence = allDetections.length > 0 
        ? allDetections.reduce((sum, d) => sum + d.confidence, 0) / allDetections.length
        : 0;

      analysis.qualityScore = analysis.frames.length > 0
        ? analysis.frames.reduce((sum, f) => sum + f.qualityScore, 0) / analysis.frames.length
        : 0;

      analysis.processingDuration = Date.now() - startTime;
      analysis.status = 'completed';
      analysis.completedAt = new Date();

      // Store analysis results in database
      await this.storeAnalysisResults(analysis);

      console.log(`Video analysis completed: ${analysis.totalDetections} total detections, ${analysis.threatDetections} threats`);

      return analysis;

    } catch (error) {
      console.error('Video analysis failed:', error);
      analysis.status = 'failed';
      analysis.error = error instanceof Error ? error.message : String(error);
      analysis.processingDuration = Date.now() - startTime;

      throw error;
    }
  }

