  /**
   * Convert AI detection results to DetectionResult format for overlay rendering
   */
  convertToDetectionResult(
    cameraId: string,
    frameDetections: AIDetectionResult[] = [],
    threatDetections: any[] = [],
    frameWidth?: number,
    frameHeight?: number
  ): DetectionResult {
    const timestamp = Date.now();
    const boxes: DetectionBoundingBox[] = [];
    let hasPixelCoordinates = false;

    // Convert frame analysis detections
    frameDetections.forEach(detection => {
      if (detection.boundingBox) {
        const normalized = detection.boundingBox.normalized ?? false;
        if (!normalized) hasPixelCoordinates = true;

        const box: DetectionBoundingBox = {
          x: detection.boundingBox.x,
          y: detection.boundingBox.y,
          w: detection.boundingBox.width,
          h: detection.boundingBox.height,
          normalized,
          label: detection.objectClass || detection.detectionType || 'unknown',
          confidence: detection.confidence,
          severity: this.mapSeverityLevel(detection.severity),
          color: this.assignColorBySeverity(detection.severity, detection.threatType)
        };
        boxes.push(box);
      }
    });

    // Convert threat assessment detections (if they have spatial data)
    threatDetections.forEach(threat => {
      if (threat.boundingBox || threat.location) {
        const normalized = threat.boundingBox?.normalized ?? threat.location?.normalized ?? false;
        if (!normalized) hasPixelCoordinates = true;

        const box: DetectionBoundingBox = {
          x: threat.boundingBox?.x || threat.location?.x || 0,
          y: threat.boundingBox?.y || threat.location?.y || 0,
          w: threat.boundingBox?.width || threat.location?.width || 50,
          h: threat.boundingBox?.height || threat.location?.height || 50,
          normalized,
          label: threat.type || threat.threatType || 'threat',
          confidence: threat.confidence || 0.8,
          severity: this.mapSeverityLevel(threat.severity || threat.riskLevel),
          color: this.assignColorBySeverity(threat.severity || threat.riskLevel, threat.type)
        };
        boxes.push(box);
      }
    });

    const result: DetectionResult = {
      cameraId,
      ts: timestamp,
      boxes
    };

    // Add frame dimensions if any coordinates are in pixels
    if (hasPixelCoordinates && frameWidth && frameHeight) {
      result.frameWidth = frameWidth;
      result.frameHeight = frameHeight;
    }

    return result;
  }

  /**
   * Map various severity formats to ThreatSeverity enum
   * Fixed to prevent overlapping conditions and ensure accurate mappings
   */
  private mapSeverityLevel(severity: string | undefined): ThreatSeverity {
    if (!severity) return 'medium';

    const severityLower = severity.toLowerCase().trim();

    // Exact matches first (highest priority)
    if (severityLower === 'critical' || severityLower === 'emergency') return 'critical';
    if (severityLower === 'high') return 'high';
    if (severityLower === 'medium' || severityLower === 'moderate') return 'medium';
    if (severityLower === 'low' || severityLower === 'info') return 'low';

    // Partial matches with priority order (critical > high > medium > low)
    // Check for critical keywords first
    if (severityLower.includes('critical') || severityLower.includes('emergency') || severityLower.includes('severe')) {
      return 'critical';
    }

    // Check for high keywords (but not if critical already matched)
    if (severityLower.includes('high') || severityLower.includes('urgent') || severityLower.includes('important')) {
      return 'high';
    }

    // Check for medium keywords
    if (severityLower.includes('medium') || severityLower.includes('moderate') || severityLower.includes('normal')) {
      return 'medium';
    }

    // Check for low keywords
    if (severityLower.includes('low') || severityLower.includes('minor') || severityLower.includes('info')) {
      return 'low';
    }

    return 'medium'; // Default fallback
  }

  /**
   * Assign color coding based on threat severity and type
   */
  private assignColorBySeverity(severity: string | undefined, threatType: string | undefined): string {
    // Color mapping for different threat levels
    const severityColors = {
      critical: '#DC2626', // Red
      high: '#EA580C',     // Orange-red
      medium: '#D97706',   // Orange
      low: '#059669'       // Green
    };

    // Special color coding for specific threat types
    const threatTypeColors = {
      theft: '#DC2626',
      violence: '#7C2D12',
      weapons: '#991B1B',
      unauthorized_access: '#92400E',
      suspicious_behavior: '#D97706'
    };

    // Priority: specific threat type colors, then severity colors
    if (threatType && threatTypeColors[threatType as keyof typeof threatTypeColors]) {
      return threatTypeColors[threatType as keyof typeof threatTypeColors];
    }

    const mappedSeverity = this.mapSeverityLevel(severity);
    return severityColors[mappedSeverity] || severityColors.medium;
  }

  /**
   * Store DetectionResult for real-time overlay tracking
   */
  async storeDetectionResult(detectionResult: DetectionResult): Promise<void> {
    try {
      // Store each detection box as an individual AI detection record
      for (const box of detectionResult.boxes) {
        await storage.createAiDetection({
          storeId: '', // Will be filled by calling context
          cameraId: detectionResult.cameraId,
          detectionType: this.inferDetectionType(box.label),
          objectClass: box.label,
          threatType: this.inferThreatType(box.label, box.severity),
          confidence: box.confidence.toString(),
          boundingBox: {
            x: box.x,
            y: box.y,
            width: box.w,
            height: box.h,
            normalized: box.x <= 1 && box.y <= 1 // Detect normalized coordinates
          },
          modelName: 'overlay-detection',
          modelVersion: '1.0',
          frameTimestamp: new Date(detectionResult.ts),
          metadata: {
            originalImagePath: `overlay_${detectionResult.ts}`,
            alertThreshold: this.mapSeverityToThreshold(box.severity),
            environmentalFactors: {
              lighting: 'unknown'
            }
          }
        });
      }
    } catch (error) {
      console.error('Failed to store detection result:', error);
      // Non-critical error - don't throw
    }
  }

  /**
   * Infer detection type from label
   */
  private inferDetectionType(label: string): string {
    const lowerLabel = label.toLowerCase();
    if (lowerLabel.includes('person') || lowerLabel.includes('people')) return 'person';
    if (lowerLabel.includes('weapon') || lowerLabel.includes('gun') || lowerLabel.includes('knife')) return 'threat';
    if (lowerLabel.includes('suspicious') || lowerLabel.includes('behavior')) return 'behavior';
    return 'object';
  }

  /**
   * Infer threat type from label and severity
   */
  private inferThreatType(label: string, severity: ThreatSeverity): string | undefined {
    const lowerLabel = label.toLowerCase();
    if (lowerLabel.includes('weapon') || lowerLabel.includes('gun') || lowerLabel.includes('knife')) return 'weapons';
    if (lowerLabel.includes('theft') || lowerLabel.includes('steal')) return 'theft';
    if (lowerLabel.includes('violence') || lowerLabel.includes('fight')) return 'violence';
    if (lowerLabel.includes('suspicious')) return 'suspicious_behavior';
    if (severity === 'critical' || severity === 'high') return 'suspicious_behavior';
    return undefined;
  }

  /**
   * Map severity level to threshold for metadata
   */
  private mapSeverityToThreshold(severity: ThreatSeverity): number {
    switch (severity) {
      case 'critical': return 0.9;
      case 'high': return 0.8;
      case 'medium': return 0.6;
      case 'low': return 0.4;
      default: return 0.5;
    }
  }

  /**
   * BEHAVIORAL PATTERN LEARNING SYSTEM INTEGRATION
   * Process behavioral patterns from frame analysis results
   */

  /**
   * Main behavioral pattern processing method
   * Extracts behavioral metrics and processes them through the behavioral learning pipeline
   */
  async processBehavioralPatterns(
    frameResult: FrameAnalysisResult,
    framePath: string,
    context: { storeId: string; cameraId: string; area?: string }
  ): Promise<void> {
    const startTime = Date.now();

    try {
      // Extract behavioral metrics from frame analysis
      const behaviorMetrics = await this.extractBehaviorMetrics(frameResult, context);

      // Process each detected behavioral event
      for (const metrics of behaviorMetrics) {
        // Create behavioral event in database
        const behaviorEvent = await this.emitBehaviorEvent(metrics, context);

        if (behaviorEvent) {
          // Update baseline in real-time (streaming mode)
          await baselineBuilder.updateBaselineStreaming(behaviorEvent);

          // Perform anomaly detection
          const anomalies = await anomalyDetector.detectAnomalies(behaviorEvent);

          // Process any detected anomalies through alert system
          for (const anomaly of anomalies) {
            if (anomaly.isAnomaly) {
              await behavioralAlertEngine.processAnomalyEvent({
                id: `anomaly_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
                storeId: context.storeId,
                cameraId: context.cameraId,
                behaviorEventId: behaviorEvent.id,
                anomalyType: 'statistical_outlier',
                severity: anomaly.severity,
                deviationScore: anomaly.deviationScore,
                baselineProfileId: anomaly.baselineProfile?.id,
                alertGenerated: false,
                metadata: {
                  confidence: anomaly.confidence,
                  description: anomaly.description,
                  recommendedActions: anomaly.recommendedActions
                },
                timestamp: behaviorEvent.timestamp
              });
            }
          }
        }
      }

      const processingTime = Date.now() - startTime;
      console.log(`Behavioral pattern processing completed in ${processingTime}ms`);

      // Ensure processing stays under 200ms requirement
      if (processingTime > 200) {
        console.warn(`Behavioral processing exceeded 200ms target: ${processingTime}ms`);
      }

    } catch (error) {
      console.error("Error in behavioral pattern processing:", error);
      throw error;
    }
  }

  /**
   * Extract behavioral metrics from frame analysis result
   * Implements the BehaviorMetrics interface from the requirements
   */
  async extractBehaviorMetrics(
    frameResult: FrameAnalysisResult,
    context: { storeId: string; cameraId: string; area?: string }
  ): Promise<Array<{
    eventType: string;
    confidence: number;
    metadata: any;
    area?: string;
  }>> {
    const behaviorMetrics: Array<{
      eventType: string;
      confidence: number;
      metadata: any;
      area?: string;
    }> = [];

    // Extract crowd density metrics
    if (frameResult.crowdDensity) {
      const peopleCount = this.estimatePeopleCount(frameResult.detections);
      behaviorMetrics.push({
        eventType: 'crowd_density',
        confidence: 0.8,
        metadata: {
          peopleCount,
          crowdLevel: frameResult.crowdDensity,
          densityScore: this.calculateDensityScore(peopleCount, frameResult.crowdDensity)
        },
        area: context.area || 'default'
      });
    }

    // Extract motion intensity metrics
    if (frameResult.motionLevel) {
      const motionIntensity = this.calculateMotionIntensity(frameResult.motionLevel);

      // Only emit if motion is significant
      if (motionIntensity > 0.3) {
        behaviorMetrics.push({
          eventType: 'motion_spike',
          confidence: 0.7,
          metadata: {
            motionIntensity,
            motionLevel: frameResult.motionLevel,
            rapidMovement: motionIntensity > 0.8
          },
          area: context.area || 'default'
        });
      }
    }

    // Extract loitering behavior from detections
    const loiteringEvents = this.detectLoiteringBehavior(frameResult.detections);
    behaviorMetrics.push(...loiteringEvents.map(event => ({
      ...event,
      area: context.area || 'default'
    })));

    // Extract dwell time patterns
    const dwellTimeEvents = this.detectDwellTimePatterns(frameResult.detections);
    behaviorMetrics.push(...dwellTimeEvents.map(event => ({
      ...event,
      area: context.area || 'default'
    })));

    // Extract unusual gathering patterns
    const gatheringEvents = this.detectUnusualGatherings(frameResult.detections);
    behaviorMetrics.push(...gatheringEvents.map(event => ({
      ...event,
      area: context.area || 'default'
    })));

    return behaviorMetrics;
  }

  /**
   * Create behavioral event in database
   */
  private async emitBehaviorEvent(
    metrics: {
      eventType: string;
      confidence: number;
      metadata: any;
      area?: string;
    },
    context: { storeId: string; cameraId: string }
  ): Promise<any> {
    const behaviorEventData: InsertBehaviorEvent = {
      storeId: context.storeId,
      cameraId: context.cameraId,
      eventType: metrics.eventType,
      area: metrics.area || 'default',
      confidence: metrics.confidence,
      metadata: metrics.metadata,
      timestamp: new Date(),
      processedAt: new Date()
    };

    try {
      const behaviorEvent = await storage.createBehaviorEvent(behaviorEventData);
      console.log(`Behavioral event emitted: ${metrics.eventType} with confidence ${metrics.confidence}`);
      return behaviorEvent;
    } catch (error) {
      console.error("Failed to emit behavioral event:", error);
      return null;
    }
  }

  /**
   * Comprehensive behavioral pattern analysis method as specified in requirements
   */
  async analyzeBehavioralPatterns(
    detectionResult: DetectionResult,
    cameraId: string,
    storeId: string
  ): Promise<{
    loiteringDuration?: number;
    crowdDensity?: number;
    motionIntensity?: number;
    dwellTime?: number;
    entryExitPatterns?: {
      entryPoint: string;
      exitPoint: string;
      duration: number;
    };
    unusualGathering?: boolean;
    rapidMovement?: boolean;
  }> {
    const behaviorMetrics: any = {};

    try {
      // Convert DetectionResult to frame-like structure for analysis
      const mockFrame: FrameAnalysisResult = {
        frameId: `detection_${detectionResult.ts}`,
        frameNumber: 0,
        timestamp: detectionResult.ts,
        detections: detectionResult.boxes.map((box, index) => ({
          id: `det_${index}`,
          detectionType: this.inferDetectionType(box.label),
          objectClass: box.label,
          confidence: box.confidence,
          boundingBox: {
            x: box.x,
            y: box.y,
            width: box.w,
            height: box.h,
            normalized: box.x <= 1 && box.y <= 1
          },
          description: box.label,
          severity: box.severity,
          frameTimestamp: detectionResult.ts,
          processingTime: 0
        })),
        qualityScore: 0.8,
        lightingConditions: 'good',
        motionLevel: 'medium',
        crowdDensity: this.inferCrowdDensity(detectionResult.boxes.length),
        modelUsed: 'overlay-detection',
        processingTime: 0
      };

      // Extract behavioral metrics
      const extractedMetrics = await this.extractBehaviorMetrics(mockFrame, { 
        storeId, 
        cameraId, 
        area: 'overlay_detection' 
      });

      // Process metrics into the required format
      for (const metric of extractedMetrics) {
        switch (metric.eventType) {
          case 'crowd_density':
            behaviorMetrics.crowdDensity = metric.metadata.peopleCount;
            break;
          case 'motion_spike':
            behaviorMetrics.motionIntensity = metric.metadata.motionIntensity;
            behaviorMetrics.rapidMovement = metric.metadata.rapidMovement;
            break;
          case 'loitering':
            behaviorMetrics.loiteringDuration = metric.metadata.duration;
            break;
          case 'dwell_time':
            behaviorMetrics.dwellTime = metric.metadata.duration;
            break;
          case 'unusual_gathering':
            behaviorMetrics.unusualGathering = true;
            break;
        }
      }

      return behaviorMetrics;
    } catch (error) {
      console.error("Error analyzing behavioral patterns:", error);
      return {};
    }
  }

  // Helper methods for behavioral metrics extraction

  private estimatePeopleCount(detections: AIDetectionResult[]): number {
    return detections.filter(d => d.detectionType === 'person').length;
  }

  private calculateDensityScore(peopleCount: number, crowdLevel: string): number {
    const densityMap = { empty: 0, sparse: 0.25, moderate: 0.5, dense: 0.8 };
    const baseDensity = densityMap[crowdLevel as keyof typeof densityMap] || 0.3;
    return Math.min(1.0, baseDensity + (peopleCount * 0.1));
  }

  private calculateMotionIntensity(motionLevel: string): number {
    const intensityMap = { low: 0.2, medium: 0.5, high: 0.8 };
    return intensityMap[motionLevel as keyof typeof intensityMap] || 0.3;
  }

  private detectLoiteringBehavior(detections: AIDetectionResult[]): Array<{
    eventType: string;
    confidence: number;
    metadata: any;
  }> {
    const events = [];
    const loiteringDetections = detections.filter(d => 
      d.behaviorType === 'loitering' || 
      (d.detectionType === 'person' && d.confidence > 0.7)
    );

    if (loiteringDetections.length > 0) {
      events.push({
        eventType: 'loitering',
        confidence: Math.max(...loiteringDetections.map(d => d.confidence)),
        metadata: {
          duration: 30, // Estimated duration in seconds
          detectionCount: loiteringDetections.length,
          averageConfidence: loiteringDetections.reduce((sum, d) => sum + d.confidence, 0) / loiteringDetections.length
        }
      });
    }

    return events;
  }

  private detectDwellTimePatterns(detections: AIDetectionResult[]): Array<{
    eventType: string;
    confidence: number;
    metadata: any;
  }> {
    const events = [];
    const dwellingDetections = detections.filter(d => 
      d.detectionType === 'person' && d.confidence > 0.6
    );

    if (dwellingDetections.length > 0) {
      events.push({
        eventType: 'dwell_time',
        confidence: 0.7,
        metadata: {
          duration: 20, // Estimated dwell time in seconds
          personCount: dwellingDetections.length,
          zones: ['main_area'] // Could be enhanced with zone detection
        }
      });
    }

    return events;
  }

  private detectUnusualGatherings(detections: AIDetectionResult[]): Array<{
    eventType: string;
    confidence: number;
    metadata: any;
  }> {
    const events = [];
    const peopleCount = detections.filter(d => d.detectionType === 'person').length;

    // Define unusual gathering as 5+ people in close proximity
    if (peopleCount >= 5) {
      events.push({
        eventType: 'unusual_gathering',
        confidence: 0.8,
        metadata: {
          peopleCount,
          isUnusual: true,
          gatheringType: peopleCount > 10 ? 'large_crowd' : 'group_gathering'
        }
      });
    }

    return events;
  }

  private inferCrowdDensity(boxCount: number): 'empty' | 'sparse' | 'moderate' | 'dense' {
    if (boxCount === 0) return 'empty';
    if (boxCount <= 2) return 'sparse';
    if (boxCount <= 5) return 'moderate';
    return 'dense';
  }

  /**
   * Clear analysis cache
   */
  clearCache(): void {
    this.analysisCache.clear();
    this.frameCache.clear();
  }
}

export const aiVideoAnalyticsService = new AIVideoAnalyticsService();

