import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Alert, AlertDescription, AlertTitle } from "@/components/ui/alert";
import { Separator } from "@/components/ui/separator";
import { 
  LineChart, Line, AreaChart, Area, BarChart, Bar, PieChart, Pie, Cell,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer 
} from "recharts";
import { 
  TrendingUp, TrendingDown, AlertTriangle, Shield, Users, Calendar,
  Activity, Target, BarChart3, Clock, CheckCircle, XCircle,
  Brain, Lightbulb, Settings, RefreshCw, Download, Filter
} from "lucide-react";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { DatePickerWithRange } from "@/components/ui/date-range-picker";
import { apiRequest } from "@/lib/queryClient";

// Types for predictive analytics
interface RiskAssessment {
  id: string;
  storeId: string;
  overallRiskScore: number;
  riskLevel: 'very_low' | 'low' | 'medium' | 'high' | 'critical';
  contributingFactors: {
    historicalIncidents: number;
    timeOfDay: number;
    dayOfWeek: number;
    seasonalPattern: number;
    staffingLevel: number;
    recentTrends: number;
  };
  confidence: number;
  recommendations: Array<{
    type: string;
    priority: string;
    description: string;
    estimatedImpact: number;
    implementationCost: string;
    timeframe: string;
  }>;
  nextReviewDate: string;
}

interface SeasonalAnalysis {
  id: string;
  timespan: string;
  patterns: {
    seasonal: Array<{
      period: string;
      incidentRate: number;
      commonIncidentTypes: string[];
      peakTimes: string[];
      riskFactors: string[];
      mitigationStrategies: string[];
    }>;
    weekly: Array<{
      dayOfWeek: string;
      averageIncidents: number;
      peakHours: string[];
    }>;
    daily: Array<{
      hour: number;
      incidentCount: number;
      riskLevel: string;
    }>;
  };
  predictions: {
    nextPeakPeriod: string;
    expectedIncidentIncrease: number;
    recommendedPreparations: string[];
  };
  confidence: number;
  dataQuality: string;
}

interface StaffingRecommendation {
  id: string;
  storeId: string;
  timeframeStart: string;
  timeframeEnd: string;
  currentStaffing: Array<{
    timeSlot: string;
    dayOfWeek: string;
    currentOfficers: number;
    skillLevels: string[];
  }>;
  recommendedStaffing: Array<{
    timeSlot: string;
    dayOfWeek: string;
    recommendedOfficers: number;
    skillRequirements: string[];
    priorityAreas: string[];
  }>;
  optimizationRationale: {
    predictedIncidentVolume: number;
    historicalWorkload: number;
    seasonalAdjustments: number;
    costEfficiencyScore: number;
  };
  expectedOutcomes: {
    incidentReductionPercent: number;
    responseTimeImprovement: number;
    costSavings: number;
    staffSatisfactionImpact: number;
  };
}

interface IncidentForecast {
  id: string;
  storeId: string;
  forecastPeriodStart: string;
  forecastPeriodEnd: string;
  predictedIncidents: Array<{
    date: string;
    predictedCount: number;
    incidentType: string;
    severity: string;
    confidence: number;
  }>;
  confidenceIntervals: {
    lower: number[];
    upper: number[];
    mean: number[];
  };
  modelAccuracy: number;
  recommendations: string[];
}

interface ModelPerformance {
  id: string;
  modelName: string;
  modelVersion: string;
  accuracyMetrics: {
    accuracy: number;
    precision: number;
    recall: number;
    f1Score: number;
    meanAbsoluteError: number;
  };
  performanceBenchmarks: {
    trainingTime: number;
    predictionLatency: number;
    memoryUsage: number;
    throughput: number;
  };
  deploymentStatus: string;
}

const RISK_LEVEL_COLORS = {
  very_low: '#22c55e',
  low: '#84cc16',
  medium: '#eab308',
  high: '#f97316',
  critical: '#ef4444'
};

const RISK_LEVEL_NAMES = {
  very_low: 'Very Low',
  low: 'Low',
  medium: 'Medium',
  high: 'High',
  critical: 'Critical'
};

